import { ApexOptions } from "apexcharts";
import React, { Component, useEffect, useState } from "react";
import Chart from "react-apexcharts";

function CandleStickChart(props: any) {
  const [options, setOptions] = useState<ApexOptions>({
    chart: {
      type: "candlestick",
    },
    title: {
      text: "CandleStick Chart",
      align: "left",
    },
    xaxis: {
      type: "datetime",
    },
    yaxis: {
      tooltip: {
        enabled: true,
      },
    },
  });

  const [series, setSeries] = useState({
    series: [
      {
        data: [{ x: Date, y: [] }],
      },
    ],
  });

  useEffect(() => {
    loadData();
  }, []);

  const loadData = async () => {
    const response = await fetch(
      "https://poloniex.com/public?command=returnChartData&currencyPair=USDT_ETH&start=1435699200&end=9999999999&period=86400"
    );
    const dataPoints = await response.json();
    /*
    const fulldataArr = dataPoints.map((point: any) => {
      return {
        x: new Date(point.date),
        y: [point.open, point.high, point.low, point.close],
      };
    });
    */

    const quarterDataArr = extractData(dataPoints);

    setSeries({
      series: [
        {
          data: [...quarterDataArr],
        },
      ],
    });
  };

  function extractData(dataPoints: any) {
    let arr: any[] = [];
    const totalData = dataPoints.length;
    const startPoint = (totalData / 4) * 3;
    for (let i = startPoint; i < totalData; i++) {
      const d = new Date(dataPoints[i].date);
      arr.push({
        x: d,
        y: [
          dataPoints[i].open,
          dataPoints[i].high,
          dataPoints[i].low,
          dataPoints[i].close,
        ],
      });
    }
    return arr;
  }

  return (
    <div className="app">
      <div className="row">
        <div className="mixed-chart">
          <Chart
            options={options}
            series={series.series}
            type="candlestick"
            width="800"
          />
        </div>
      </div>
    </div>
  );
}

export default CandleStickChart;
