import React, { useEffect, useState } from "react";

export default function Ticker() {
  const [data, setData] = useState<any>();
  const [error, setError] = useState(false);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    setError(false);
    const jsonData = fetchData();
    setData(jsonData);
    setLoading(false);
    /*
    const intervalId = setInterval(() => {
      //assign interval to a variable to clear it.
      setError(false);
      setLoading(true);
      (async () => {
        const users = await fetchData();
      })();
    }, 5000);
    return () => clearInterval(intervalId); //This is important
    */
  }, []);

  async function fetchData() {
    try {
      const response = await fetch(
        "https://poloniex.com/public?command=returnTicker",
        {
          headers: {
            Accept: "application/json",
          },
        }
      );
      const json = await response.json();
      return json;
    } catch (err) {
      console.log("Error" + err);
    }

    /*
      .then((response) => response.json())
      .catch(() => {
        console.log("Error");
        setError(true);
      })
      .then((data) => {
        setData(data.USDT_ETH);
        console.log("Data Added" + data);
      });
      */
  }

  return (
    <div className="card">
      <div className="cardContainer"></div>
      <div className="right">
        <h6>High 24h</h6>
        <h2 className="red">{data.high24hr}</h2>
        <h6>Low 24h</h6>
        <h2 className="green">{data.low24hr}</h2>
      </div>
      <div className="left">
        <span className="title">Etheruem Live Stats</span>
        <p className="live-icon"></p>
        <h1 className="red">${data.last}</h1>
        <h4 className="red">{data.percentChange}</h4>
      </div>
    </div>
  );
}
