import React, { useEffect, useState } from "react";

export default function Ticker() {
  const [data, setData] = useState<any>(null);
  const [error, setError] = useState(false);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    setError(false);
    setLoading(true);
    const jsonData = fetchData();
    setData({ ...jsonData });
    /*
    const intervalId = setInterval(() => {
      //assign interval to a variable to clear it.
      setError(false);
      setLoading(true);
      (async () => {
        const users = await fetchData();
      })();
    }, 5000);
    return () => clearInterval(intervalId); //This is important
    */
  }, []);

  useEffect(() => {
    setLoading(false);
  }, [data]);

  async function fetchData() {
    try {
      const response = await fetch(
        "https://poloniex.com/public?command=returnTicker",
        {
          headers: {
            Accept: "application/json",
          },
        }
      );
      const json = await response.json();
      return json;
    } catch (err) {
      console.log("Error@" + err);
      setError(true);
      return null;
    }

    /*
      .then((response) => response.json())
      .catch(() => {
        console.log("Error");
        setError(true);
      })
      .then((data) => {
        setData(data.USDT_ETH);
        console.log("Data Added" + data);
      });
      */
  }

  return (
    <div className="card">
      <div className="cardContainer"></div>
      <div className="right">
        {loading ? (
          <h6>Fetching...</h6>
        ) : (
          <>
            <h6>High 24h</h6>
            <h2 className="red">{data.USDT_ETH.high24hr}</h2>
            <h6>Low 24h</h6>
            <h2 className="green">{data.USDT_ETH.low24hr}</h2>
          </>
        )}
      </div>
      <div className="left">
        <span className="title">
          {error ? "Something Went Wrong" : "Etheruem Live Stats"}
        </span>
        <p className={"live-icon " + (error ? "r" : "g")}></p>
        {loading ? (
          <h1>Fetching...</h1>
        ) : (
          <>
            <h1 className="red">${data.USDT_ETH.last}</h1>
            <h4 className="red">{data.USDT_ETH.percentChange}</h4>
          </>
        )}
      </div>
    </div>
  );
}
