import React, { useEffect, useState } from "react";

export default function Ticker() {
  const [data, setData] = useState({ USDT_ETH: {} });
  const [error, setError] = useState(false);

  useEffect(() => {
    const intervalId = setInterval(() => {
      //assign interval to a variable to clear it.
      setState((state) => ({ data: state.data, error: false, loading: true }));
      fetch(url)
        .then((data) => data.json())
        .then((obj) =>
          Object.keys(obj).map((key) => {
            let newData = obj[key];
            newData.key = key;
            return newData;
          })
        )
        .then((newData) =>
          setState({ data: newData, error: false, loading: false })
        )
        .catch(function (error) {
          console.log(error);
          setState({ data: null, error: true, loading: false });
        });
    }, 5000);

    return () => clearInterval(intervalId); //This is important
  }, [data]);

  return (
    <div className="card">
      <div className="cardContainer"></div>
      <div className="right">
        <h6>High 24h</h6>
        <h2 className="red">$2044.2373</h2>
        <h6>Low 24h</h6>
        <h2 className="green">$1984.2865</h2>
      </div>
      <div className="left">
        <span className="title">Etheruem Live Stats</span>
        <p className="live-icon"></p>
        <h1 className="red">$2301.2323</h1>
        <h4 className="red">0.0362%</h4>
      </div>
    </div>
  );
}
