  }

  return (
    <div className="card">
      <div className="cardContainer"></div>
      <div className="right">
        <h6>High 24h</h6>
        <h2 className="red">$2044.2373</h2>
        <h6>Low 24h</h6>
        <h2 className="green">$1984.2865</h2>
      </div>
