        setError(true);
      })
      .then((data) => {
        setData(data.USDT_ETH);
        console.log("Data Added" + data);
      });
      */
  }

  return (
    <div className="card">
      <div className="cardContainer"></div>
      <div className="right">
        <h6>High 24h</h6>
        <h2 className="red">{data.high24hr}</h2>
        <h6>Low 24h</h6>
        <h2 className="green">{data.low24hr}</h2>
      </div>
      <div className="left">
        <span className="title">Etheruem Live Stats</span>
        <p className="live-icon"></p>
        <h1 className="red">$2301.2323</h1>
        <h4 className="red">0.0362%</h4>
      </div>
    </div>
  );
}
